mapper.select:
SELECT id AS carId, lat AS latitude , lon AS longitude, date , wind_direction as windDirection , csv_filename AS csvFilename
FROM car_detail
WHERE date BETWEEN #{startTime} AND #{endTime} AND car_code like #{selectCar}

SELECT  lat AS latitude , lon AS longitude
FROM car_sift
WHERE date BETWEEN #{startTime} AND #{endTime} AND car_code like #{selectCar}

SELECT DATE_FORMAT(date, '%Y-%m-%d') AS reg_date
FROM car_sift
WHERE car_code = #{carCode}
GROUP BY reg_date
ORDER BY reg_date DESC;

SELECT id AS carId, lat AS latitude, lon AS longitude, date, wind_direction AS windDirection, csv_filename AS csvFilename
FROM car_detail
WHERE date BETWEEN #{startTime} AND #{endTime}
  AND car_code LIKE #{selectCar}
  AND date > #{lastQueryTime}

SELECT  lat AS latitude , lon AS longitude
FROM car_sift
WHERE date BETWEEN #{startTime} AND #{endTime}
  AND car_code LIKE #{selectCar}
  AND date > #{lastQueryTime}

SELECT c.id AS complaintId, c.userName, c.contents AS context, c.province_city_name AS provinceCityName, c.postal_code AS postalCode, c.street_address AS streetAddress, c.lat AS latitude, c.lon AS longitude, c.wind_direction AS windDirection, c.wind_speed AS windSpeed, c.odor, c.odorintensity AS odorIntensity, c.date, o.odorName AS odorName
FROM complaint c LEFT JOIN odor_type o ON c.odor = o.Id
WHERE c.id =#{complaintId}

SELECT id
FROM odor_type
WHERE odorName = #{odorName}

SELECT c.id AS complaintId, c.userName, c.contents AS context, c.province_city_name AS provinceCityName, c.postal_code AS postalCode, c.street_address AS streetAddress, c.lat AS latitude, c.lon AS longitude, c.wind_direction AS windDirection, c.wind_speed AS windSpeed, c.odor, c.odorintensity AS odorIntensity, c.date, o.odorName AS odorName FROM complaint c LEFT JOIN odor_type o ON c.odor = o.Id WHERE
c.date BETWEEN #{startDateTime} AND #{endDateTime}
c.date <= #{endDateTime}
c.date >= #{startDateTime}
AND o.odorName = #{odorName}
AND c.odorIntensity = #{odorIntensity}

mapper.insert:
INSERT INTO complaint (
	username, contents, lat, lon, odor, odorintensity, date
) VALUES (
			 #{userName}, #{context}, #{latitude}, #{longitude}, #{odor}, #{odorIntensity}, #{date}
		 )

mapper.select:
SELECT c.id AS complaintId, c.userName, c.contents AS context, c.province_city_name AS provinceCityName, c.postal_code AS postalCode, c.street_address AS streetAddress, c.lat AS latitude, c.lon AS longitude, c.wind_direction AS windDirection, c.wind_speed AS windSpeed, c.odor, c.odorintensity AS odorIntensity, c.date, o.odorName AS odorName FROM complaint c LEFT JOIN odor_type o ON o.Id = #{odorId} WHERE
c.date BETWEEN #{startDateTime} AND #{endDateTime}
c.date <= #{endDateTime}
c.date >= #{startDateTime}
AND c.odor = #{odorId}
AND c.odorIntensity = #{odorIntensity}
